toolUseResult.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  :root {
    /* Deep dark theme with purple/pink fashion tech vibe */
    --background: 240 15% 6%;
    --foreground: 270 20% 95%;

    --card: 240 15% 10%;
    --card-foreground: 270 20% 95%;

    --popover: 240 15% 10%;
    --popover-foreground: 270 20% 95%;

    --primary: 270 80% 65%;
    --primary-foreground: 270 20% 98%;

    --secondary: 320 70% 60%;
    --secondary-foreground: 320 20% 98%;

    --muted: 240 15% 18%;
    --muted-foreground: 240 10% 55%;

    --accent: 280 60% 55%;
    --accent-foreground: 280 20% 98%;

    --destructive: 0 72% 51%;
    --destructive-foreground: 0 0% 100%;

    --border: 270 20% 18%;
    --input: 270 20% 15%;
    --ring: 270 80% 65%;

    --radius: 0.75rem;

    /* Custom gradients */
    --gradient-primary: linear-gradient(135deg, hsl(270 80% 65%), hsl(320 70% 60%));
    --gradient-glow: linear-gradient(135deg, hsl(270 80% 65% / 0.4), hsl(320 70% 60% / 0.4));
    --gradient-card: linear-gradient(145deg, hsl(240 15% 12%), hsl(240 15% 8%));
    --gradient-border: linear-gradient(135deg, hsl(270 80% 65% / 0.5), hsl(320 70% 60% / 0.5));

    /* Custom shadows */
    --shadow-glow: 0 0 30px hsl(270 80% 65% / 0.3);
    --shadow-glow-lg: 0 0 60px hsl(270 80% 65% / 0.4);
    --shadow-elegant: 0 20px 40px -10px hsl(240 15% 0% / 0.5);
  }

  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground antialiased;
    font-feature-settings: "rlig" 1, "calt" 1;
  }
}

@layer components {
  /* Glass card effect */
  .glass-card {
    @apply bg-card/60 backdrop-blur-xl border border-border/50 rounded-2xl;
    background: linear-gradient(145deg, hsl(var(--card) / 0.8), hsl(var(--card) / 0.4));
  }

  /* Gradient border effect */
  .gradient-border {
    position: relative;
  }

  .gradient-border::before {
    content: '';
    position: absolute;
    inset: 0;
    border-radius: inherit;
    padding: 1px;
    background: var(--gradient-border);
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    mask-composite: exclude;
    pointer-events: none;
  }

  /* Glow text */
  .glow-text {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    filter: drop-shadow(0 0 20px hsl(270 80% 65% / 0.5));
  }

  /* Shimmer loading effect */
  .shimmer {
    background: linear-gradient(
      90deg,
      hsl(var(--muted)) 0%,
      hsl(var(--muted-foreground) / 0.1) 50%,
      hsl(var(--muted)) 100%
    );
    background-size: 200% 100%;
    animation: shimmer 1.5s infinite;
  }

  /* Upload zone styles */
  .upload-zone {
    @apply relative rounded-2xl border-2 border-dashed border-border/50 transition-all duration-300;
    background: linear-gradient(145deg, hsl(var(--card) / 0.5), hsl(var(--card) / 0.2));
  }

  .upload-zone:hover,
  .upload-zone.drag-over {
    @apply border-primary/60;
    box-shadow: var(--shadow-glow);
    background: linear-gradient(145deg, hsl(var(--primary) / 0.1), hsl(var(--card) / 0.3));
  }

  /* Style card hover */
  .style-card {
    @apply glass-card p-4 cursor-pointer transition-all duration-300;
  }

  .style-card:hover {
    @apply scale-[1.02];
    box-shadow: var(--shadow-glow);
  }

  .style-card.selected {
    @apply border-primary/80;
    box-shadow: var(--shadow-glow);
  }

  /* Primary button gradient */
  .btn-primary {
    @apply relative px-6 py-3 rounded-xl font-semibold text-primary-foreground overflow-hidden transition-all duration-300;
    background: var(--gradient-primary);
  }

  .btn-primary:hover {
    @apply scale-[1.02];
    box-shadow: var(--shadow-glow);
  }

  .btn-primary:disabled {
    @apply opacity-50 cursor-not-allowed scale-100;
    box-shadow: none;
  }

  /* Result image container */
  .result-container {
    @apply relative rounded-2xl overflow-hidden;
    background: var(--gradient-card);
  }

  .result-container::before {
    content: '';
    @apply absolute inset-0 opacity-0 transition-opacity duration-500;
    background: var(--gradient-glow);
    filter: blur(40px);
  }

  .result-container.has-result::before {
    @apply opacity-100;
  }
}

@layer utilities {
  .text-gradient {
    background: var(--gradient-primary);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .bg-gradient-mesh {
    background-image:
      radial-gradient(at 40% 20%, hsl(270 80% 65% / 0.15) 0px, transparent 50%),
      radial-gradient(at 80% 0%, hsl(320 70% 60% / 0.1) 0px, transparent 50%),
      radial-gradient(at 0% 50%, hsl(280 60% 55% / 0.1) 0px, transparent 50%),
      radial-gradient(at 80% 50%, hsl(270 80% 65% / 0.08) 0px, transparent 50%),
      radial-gradient(at 0% 100%, hsl(320 70% 60% / 0.1) 0px, transparent 50%);
  }
}

